import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../Service/api.service';
import { Login } from '../../Model/Login';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit
{
   login:Login = new Login();
  constructor(private apiservice:ApiService){}

  ngOnInit(): void {

  }
doLogin(){
    let loginData = this.login.LoginForm.value;
    this.apiservice.Login(loginData).subscribe(
        res=>{
            alert("You are logged in sucessfully");
        },
        err=>{
            console.log(err);
        }
    );
}

}

<form style="margin-left: 580px; padding-top: 100px; ">
  <div>
    <h3>Login</h3>
  </div>
  <div>
    <label for="Username">Username</label>
    <input type="text" name="Username"  class=" form-control"  style="width: 50%;"/>
    <label for="Password">Password</label>
    <input type="text" name="Password"  class=" form-control"  style="width: 50%;" />
  </div>
  <div style="padding-top: 5px;">
    <button class="btn btn-primary" >Login</button>
  </div>
</form>
<a routerLink="/home">Go to home</a>
