import { Component, OnInit, ViewChild } from '@angular/core';
import { Doctor } from '../../Model/doctorModel';
import { Patient} from '../../Model/patientModel';
import { ApiService } from '../../Service/api.service';
import { PatientProblem } from '../../Model/PatinetProblem';

@Component({
  selector: 'app-patient',
  templateUrl:'./patient.component.html',
  styleUrls: ['./patient.component.css']
})
export class PatientComponent implements OnInit{
  patient :Patient=new Patient();
  problem : PatientProblem = new PatientProblem();
  doctorList:any;
  doctorId: any;
  patientList: any;
  patientRow:Patient=new Patient();
  doctorFname: any;
  doctorLname: any;
  docId: any;
  @ViewChild('closebutton')closebutton:any;
constructor(private _service:ApiService) { 
}
ngOnInit(): void {
    this.listPatient();
    this.listDoctor();
}
addPatient(){
  this._service.postPatient(this.patient).subscribe(
    res=>{
      alert("Patinet added Successfully");
      this.listPatient();
      this.closebutton.nativeElement.click();// closing modal 
    },
    err=>{
      alert("failed to add new patient");
        console.log(err);
    }
  );
}

listPatientRow(id:number) {
  this._service.getPatientById(id).subscribe(
    res => {
      this.patientRow = res;
      this.doctorFname=res.Doctor.FirstName;
      this.docId=res.Doctor.DoctorId;
      this.doctorLname=res.Doctor.LastName;
      console.log(this.patientRow);
    },
    err=>{
      console.log(err);
    }
  );
}
addPatientProblem(){
  this.lis
let problemValue =this.problem.ProblemGroup.value
this._service.postPatientProblem(problemValue).subscribe(
  res=>{

  },
  err=>{
    console.log(err);
  }
)
}
listPatient(){
  this._service.getPatient().subscribe(
    res=>{
      this.patientList=res;
    },
    err=>{

    }
  )
}
listDoctor(){
  this._service.getDoctor().subscribe(
    res =>{
     this.doctorList=res;

    },
    err =>{
     console.log(err);
    }
  );
}
onDoctorChange(event:any){
  this.doctorId=event.target.value;
  this.patient.DoctorId=this.doctorId;
}
}

<div>
    <button  style="float:right; margin-right:10px;margin-bottom: 30px;" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#PatientModal">Add Patient</button>
</div>
<div>
    <table class="table table-hover" style="margin-left: 10px;">
      <thead>
          <th>S.N</th>
          <th>Patient Name</th>
          <th>Address</th>
          <th>Phone Number</th>
          <th>Age</th>
          <th>Doctor</th>
          <th>Action</th>
      </thead>
      <tbody>
          <tr *ngFor="let data of patientList; let i=index;" >
            <td>{{i+1}}</td>
            <td>{{data.FirstName}}&nbsp;{{data.LastName}}</td>
            <td>{{data.Address}}</td>
            <td>{{data.Contact}}</td>
            <td>{{data.Age}}</td>
            <td>{{data.Doctor.FirstName}}&nbsp;{{data.Doctor.LastName}}</td>
            <td>
              <button class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#addProblem"
              >View Problem</button>
              <button style="margin-left: 5px;" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#editDoctorRow"
              >Edit</button>
              <button class="btn btn-danger" style="margin: left 5px;"  data-bs-toggle="modal" data-bs-target="#deleteDoctor">Delete</button>
            </td>
          </tr>
      </tbody>
  </table>
  </div>
   <!-- Modal for new adding Patient -->
   <div class="modal fade" id="PatientModal" tabindex="-1" aria-labelledby="PatientModalLabel" aria-hidden="true">
    <div class="modal-dialog modal-lg">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="PatientModalLabel">New Patient</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
            <div class="row">
             <div class="col-md-6 mb-4">
                <label for="FirstName">First Name</label>
                <input type="text"[(ngModel)]="patient.FirstName"  class="form-control">
             </div>
             <div class="col-md-6 mb-4">
                <label for="LastName">Last Name</label>
                <input type="text" [(ngModel)]="patient.LastName"  class="form-control">
             </div>
            </div>
            <div class="row">
                <div class="col-md-6 mb-4">
                   <label for="Address">Address</label>
                   <input type="text" [(ngModel)]="patient.Address" class="form-control">
                </div>
                <div class="col-md-6 mb-4">
                   <label for="PhoneNumber">PhoneNumber</label>
                   <input type="text" [(ngModel)]="patient.Contact"  class="form-control">
                </div>
            </div>
            <div class="row">
                <div class="col-md-6 mb-4">
                   <label for="Age">Age</label>
                   <input type="text" [(ngModel)]="patient.Age" class="form-control">
                </div>
                <div class="col-md-6 mb-4">
                    <label for="Doctor">Doctor</label>
                    <select name="" class="form-select" id="" (change)="onDoctorChange($event)">
                        <option value="">Choose Doctor</option>
                        <option value="{{item.DoctorId}}" *ngFor="let item of doctorList">{{item.FirstName}}&nbsp;{{item.LastName}}</option>
                    </select>
                 </div>
            </div>
        </div>
        <div class="modal-footer">
          <button type="button" #closebutton class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
          <button type="button" class="btn btn-primary" (click)="addPatient()">Submit</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Modal to add Patient Problem-->
<div class="modal fade" id="addProblem" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg">
      <div class="modal-content">
          <div class="modal-header">
              <h1 class="modal-title fs-5" id="exampleModalLabel">Add Problem</h1>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div class="modal-body">
              <form [formGroup]="problem.ProblemGroup">
              <div class="row">
                  <div class="col-md-6 mb-4">
                      <label for="Problem">Problem</label>
                      <input type="text" formControlName="Problem"   class="form-control">
                      <!-- <small *ngIf="problem.ProblemForm.controls['Description'].invalid
                      &&problem.ProblemForm.controls['Description'].touched" class="text-danger">Required Field input</small> -->
                  </div>
                  <!-- <div class="col-md-6 mb-4" style="margin-top: 23px;">
                      <button class="btn btn-primary" (click)="addPatientProblem()">Add</button>
                  </div> -->
              </div>
          </form>
          <div class="modal-footer">
              <button type="button" #closebutton class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary" (click)="addPatientProblem()" >Submit</button>
          </div>
      </div>
  </div>
</div>
