import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../Service/api.service';
import { Login } from '../../Model/Login';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit
{
   login:Login = new Login();
  constructor(private apiservice:ApiService){}

  ngOnInit(): void {

  }
doLogin(){
    let loginData = this.login.LoginForm.value;
    this.apiservice.Login(loginData).subscribe(
        res=>{
            alert("You are logged in sucessfully");
        },
        err=>{
            console.log(err);
        }
    );
}

}



<!-- Section: Design Block -->


<section class="text-center">
  <!-- Background image -->
  <div class="p-5 bg-image" style="
        background-image: url('https://mdbootstrap.com/img/new/textures/full/171.jpg');
        height: 300px;
        "></div>
  <!-- Background image -->

  <div class="card mx-4 mx-md-5 shadow-5-strong" style="
        margin-top: -100px;
        background: hsla(0, 0%, 100%, 0.8);
        backdrop-filter: blur(30px);
        ">
    <div class="card-body py-5 px-md-5">

      <div class="row d-flex justify-content-center">
        <div class="col-lg-8">
          <h2 class="fw-bold mb-5">Login for Admin Account</h2>
          <form [formGroup]="login.LoginForm" (submit)="doLogin()">

            <!-- Email input -->
            <div class="form-outline mb-4">
              <label class="form-label" for="form3Example3">Username</label>
              <input type="email" id="form3Example3" class="form-control" formControlName="Username" />

              <small class="error" *ngIf="login.LoginForm.get('Username')!.invalid 
              && login.LoginForm.get('Username')!.touched" class="text-danger">Username is required</small>
            </div>

            <!-- Password input -->
            <div class="form-outline mb-4">
              <label class="form-label" for="form3Example4">Password</label>
              <input type="password" id="form3Example4" class="form-control" formControlName="Password" />

              <small class="error" *ngIf="login.LoginForm.get('Password')!.invalid 
              && login.LoginForm.get('Password')!.touched" class="text-danger">Invalid password</small>
            </div>

            <!-- Submit button -->
            <button type="submit" class="btn btn-primary btn-block mb-4" routerLink="/Admin/AdminDashboard">
              Login
            </button>

            <!-- Register buttons -->
            <div class="text-center">
              <p>Don't have an account?<a routerLink="/Register">Sign up</a></p>
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</section>
