import { Component, OnInit, ViewChild } from '@angular/core';
import { Doctor } from '../../Model/doctorModel';
import { ApiService } from '../../Service/api.service';


@Component({
  selector: 'app-doctor',
  templateUrl: './doctor.component.html',
  styleUrls: ['./doctor.component.css']
})
export class DoctorComponent implements OnInit {
   public doctor: Doctor=new Doctor();   
   public doctorList: any;
   public doctorRow: any;
   isDoctorSelected:boolean =false;
   @ViewChild('closebutton')closebutton:any;
  doctorId: any;
  constructor( private _apiService:ApiService) {
  }
  ngOnInit(): void 
  {
    this.listDoctor();
  }
  addDoctor()
  {  
     this._apiService.postDoctor(this.doctor).subscribe(
      res=>{
        alert("Doctor added Successfully");
        this.listDoctor();
        this.closebutton.nativeElement.click();
      },
      err=>{
        alert("failed to add new doctor");
        console.log(err);
      }
     );
  }
  listDoctor(){
    this._apiService.getDoctor().subscribe(
      res =>{
       this.doctorList=res;
       //console.log(this.doctorList);
      },
      err =>{

      }
    );
  }

  selectedDoctor(id:number){
    this.isDoctorSelected=true;//to only tigger it when edit button is clicked so that editform is igonred at run time.
    this.fetchDoctorId(id);
    this._apiService.getDoctorById(id).subscribe(
      res=>{
        this.doctorRow=res;
      },
      err=>{

      }
    )
  }
  editDoctor(){
    this._apiService.updateDoctor(this.doctorId,this.doctorRow).subscribe(
      res=>{
        this.listDoctor();
        this.close.nativeElement.click();
        alert("Updated Successfully");
        

      },
      err=>{
   
      }
    )
  }
  fetchDoctorId(id:number)
  {
    this.doctorId=id;
  }
}
